import React, { Component } from "react";
import { Link } from "react-router-dom";

import "./style.css";

class Cadastro extends Component {
    state = {
        username: "",
        email: "",
        password: "",
        confsenha: "",
        error: "",
        administrador: false,
        avaliador: false,
    };

    handleCadastro = e => {
        e.preventDefault();
        alert("Tela de Cadastro");
    };

    handleInput(event) {
        const target = event.target;
        const value = target.type === 'checkbox'
        ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });

    }

    render() {
        return (
            <div className="container">
        <div className="form" onSubmit={this.handleCadastro}>
          <input
            type="text"
            placeholder="Username"
            onChange={e => this.setState({ username: e.target.value })}
          />
          <input
            type="email"
            placeholder="email@gmail.com"
            onChange={e => this.setState({ email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Senha"
            onChange={e => this.setState({ password: e.target.value })}
          />
          <input
            type="password"
            placeholder="Confirmar senha"
            onChange={e => this.setState({confsenha: e.target.value})}
          />
          <input 
            type="checkbox"
            checked={this.state.administrador}
            name="admin"
            onChange={this.handleInput}            
          />
          <Link to="/"><button type="reset">Cancelar</button></Link>
          <Link to="/"><button type="submit" >Salvar</button></Link>

        </div>
      </div>
    );
  }
}

export default Cadastro;
