import React, { Component } from "react";
import { Link } from "react-router-dom";

import "./style.css";

class Cadastro extends Component {
    state = {
        username: "",
        email: "",
        password: "",
        confsenha: "",
        error: "",
        administrador: false,
        avaliador: false
    };

    handleCadastro = e => {
        e.preventDefault();
        alert("Tela de Cadastro");
    };


    render() {
        return (
            <div className="container">
        <div className="form" onSubmit={this.handleCadastro}>
        <div className="tit"><h1>Novo Usu√°rio</h1></div>

          <form>
          <input
            type="text"
            placeholder="Username"
            onChange={e => this.setState({ username: e.target.value })}
          />
          <input
            type="email"
            placeholder="email@gmail.com"
            onChange={e => this.setState({ email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Senha"
            onChange={e => this.setState({ password: e.target.value })}
          />
          <input
            type="password"
            placeholder="Confirmar senha"
            onChange={e => this.setState({confsenha: e.target.value})}
          />
          <input 
            type="checkbox"
            placeholder="Administrador"
            onChange={e => this.setState({administrador: e.target.value})}

          />
          <div className="checks">Administrador</div>
          <input
            type="checkbox"
            placeholder="Avaliador"
            onChange={e => this.setState({avaliador: e.target.value})}
          />
          <div className="checks">Avaliador</div>

          <Link to="/"><button type="reset">Cancelar</button></Link>
          <Link to="/"><button type="submit" >Salvar</button></Link>

          </form>

        </div>
      </div>
    );
  }
}

export default Cadastro;
